    <script>
        let scrollTimeout;

        container.addEventListener("scroll", () => {
            clearTimeout(scrollTimeout);

            scrollTimeout = setTimeout(() => {
                const center = container.scrollLeft + container.offsetWidth / 2;

                let closest = null;
                let minDistance = Infinity;

                items.forEach(item => {
                    const box = item.getBoundingClientRect();
                    const itemCenter =
                        box.left + box.width / 2 + container.scrollLeft;

                    const distance = Math.abs(center - itemCenter);

                    if (distance < minDistance) {
                        minDistance = distance;
                        closest = item;
                    }
                });

                if (closest) {
                    closest.scrollIntoView({
                        behavior: "smooth",
                        inline: "center"
                    });
                }
            }, 120);
        });
    </script>


    <script>
        let currentRating = 0;

        function openRatingModal(menuId, menuName) {
            document.getElementById('menuId').value = menuId;
            document.getElementById('modalItemName').innerText = menuName;
            document.getElementById('ratingModal').classList.remove('hidden');
        }

        function closeRatingModal() {
            document.getElementById('ratingModal').classList.add('hidden');
            resetRating();
        }

        function setRating(value) {
            currentRating = value;
            document.getElementById('selectedRating').value = value;
            document.getElementById('ratingValue').innerText = value;

            const texts = ['', 'Buruk', 'Kurang', 'Cukup', 'Bagus', 'Sangat Bagus'];
            document.getElementById('ratingText').innerText = texts[value];

            document.querySelectorAll('.rating-star svg').forEach((star, index) => {
                star.classList.toggle('text-amber-400', index < value);
                star.classList.toggle('text-gray-600', index >= value);
            });
        }

        function resetRating() {
            currentRating = 0;
            document.getElementById('ratingValue').innerText = '0';
            document.getElementById('ratingText').innerText = '';
            document.getElementById('ratingComment').value = '';

            document.querySelectorAll('.rating-star svg').forEach(star => {
                star.classList.remove('text-amber-400');
                star.classList.add('text-gray-600');
            });
        }

        async function submitRating() {
            if (currentRating === 0) {
                alert('Silakan pilih rating terlebih dahulu');
                return;
            }

            const response = await fetch('{{ route('testimonials.store') }}', {
                method: 'POST',
                headers: {
                    'X-CSRF-TOKEN': document.querySelector('input[name="_token"]').value,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    menu_id: document.getElementById('menuId').value,
                    rating: currentRating,
                    review: document.getElementById('ratingComment').value
                })
            });

            const result = await response.json();

            if (!response.ok) {
                alert(result.message || 'Gagal mengirim rating');
                return;
            }

            alert(result.message);
            closeRatingModal();
        }
    </script>

@endsection
